var debug = require('debug')('server'),
    stacks = require('..');

stacks.listen(3000, function() {
  debug('stacks listening');
});

setTimeout(function() {

  for (var i = 0; i <= 200; i++) {
    stacks.request('sum numbers', { numbers: [4, 4, 10, 2, i, i/2] }, function (error, sum) {
      if (error) {
        console.error('error: ', error);
        return;
      }

      console.log('sum: ', sum)
    });
  }

}, 10000);
